import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { Bar } from 'react-chartjs-2';

function FilteredProducts() {
  const [filteredProducts, setFilteredProducts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch products from your API
    axios.get('/api/Product/products')
      .then((res) => {
        const lowQuantityProducts = res.data.filter(product => product.rquantity < 5);
        setFilteredProducts(lowQuantityProducts);
      })
      .catch((err) => console.log(err));
  }, []);

  // Define data for the bar chart
  const chartData = {
    labels: filteredProducts.map(product => product.name),
    datasets: [
      {
        label: 'Remaining Quantity',
        backgroundColor: 'rgba(75,192,192,0.2)',
        borderColor: 'rgba(75,192,192,1)',
        borderWidth: 1,
        hoverBackgroundColor: 'rgba(75,192,192,0.4)',
        hoverBorderColor: 'rgba(75,192,192,1)',
        data: filteredProducts.map(product => product.rquantity),
      },
    ],
  };

  return (
    <div>
      <h1>Low Quantity Products</h1>
      <div className="box">
        <table className="table">
          <thead>
            <tr>
              <th>Name</th>
              <th>Type</th>
              <th>Category</th>
              <th>Date</th>
              <th>Remaining Quantity</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {filteredProducts.map((product, index) => (
              <tr key={index}>
                <td>{product.name}</td>
                <td>{product.type}</td>
                <td>{product.category}</td>
                <td>{product.date}</td>
                <td>{product.rquantity}</td>
                <td>
                  <button onClick={() => navigate("/sups/SupplierOrd")}>View Suppliers</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      <div className="card">
        <div className="card-body">
          <h2>Remaining Quantity Chart</h2>
          <Bar
            data={chartData}
            width={100}
            height={50}
          />
        </div>
      </div>
    </div>
  );
}

export default FilteredProducts;
