import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Chart from 'chart.js/auto';

function FilteredProducts() {
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [chartData, setChartData] = useState(null); // State to store chart data
  const navigate = useNavigate();

  // Create a ref to the canvas element
  const chartRef = React.createRef();
  const chartInstance = React.createRef();

  useEffect(() => {
    // Fetch products from your API
    axios.get('/api/Product/products')
      .then((res) => {
        const lowQuantityProducts = res.data.filter(product => product.rquantity < 5);
        setFilteredProducts(lowQuantityProducts);

        // Destroy the previous chart if it exists
        if (chartInstance.current) {
          chartInstance.current.destroy();
        }

        // Extract data for the chart
        const productNames = lowQuantityProducts.map(product => product.name);
        const remainingQuantities = lowQuantityProducts.map(product => product.rquantity);

        // Create the chart as a pie chart
        chartInstance.current = new Chart(chartRef.current, {
          type: 'pie', // Change the chart type to 'pie'
          data: {
            labels: productNames,
            datasets: [
              {
                label: 'Remaining Quantity',
                data: remainingQuantities,
                backgroundColor: [
                  'rgba(255, 99, 132, 0.2)',
                  'rgba(54, 162, 235, 0.2)',
                  'rgba(255, 206, 86, 0.2)',
                  'rgba(75, 192, 192, 0.2)',
                  'rgba(153, 102, 255, 0.2)',
                ],
                borderColor: [
                  'rgba(255, 99, 132, 1)',
                  'rgba(54, 162, 235, 1)',
                  'rgba(255, 206, 86, 1)',
                  'rgba(75, 192, 192, 1)',
                  'rgba(153, 102, 255, 1)',
                ],
                borderWidth: 1,
              },
            ],
          },
        });

        // Set the chart data in state to persist it
        setChartData(chartInstance.current);
      })
      .catch((err) => console.log(err));
  }, []);

  return (
    <div>
      <h1>Low Quantity Products</h1>
      <div className="box">
        <table className="table">
          <thead>
            <tr>
              <th>Name</th>
              <th>Type</th>
              <th>Category</th>
              <th>Date</th>
              <th>Remaining Quantity</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {filteredProducts.map((product, index) => (
              <tr key={index}>
                <td>{product.name}</td>
                <td>{product.type}</td>
                <td>{product.category}</td>
                <td>{product.date}</td>
                <td>{product.rquantity}</td>
                <td>
                  <button onClick={() => navigate("/sups/SupplierOrd")}>View Suppliers</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <div className="chart">
          <canvas ref={chartRef} width="400" height="200"></canvas>
        </div>
      </div>
    </div>
  );
}

export default FilteredProducts;
