import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import Chart from 'chart.js/auto';

function FilteredProducts() {
  const [filteredProducts, setFilteredProducts] = useState([]);
  const chartRef = useRef(null);
  const [showChart, setShowChart] = useState(false);

  useEffect(() => {
    // Fetch products from your API
    axios.get('/api/Product/products')
      .then((res) => {
        const lowQuantityProducts = res.data.filter(product => product.rquantity < 5);
        setFilteredProducts(lowQuantityProducts);
      })
      .catch((err) => console.log(err));
  }, []);

  const renderChart = () => {
    if (chartRef.current && filteredProducts.length > 0) {
      const productNames = filteredProducts.map(product => product.name);
      const productQuantities = filteredProducts.map(product => product.rquantity);

      const chartData = {
        labels: productNames,
        datasets: [
          {
            label: 'Remaining Quantity',
            data: productQuantities,
            backgroundColor: 'rgba(75, 192, 192, 0.2)',
            borderColor: 'rgba(75, 192, 192, 1)',
            borderWidth: 1,
          },
        ],
      };

      const ctx = chartRef.current.getContext('2d');
      new Chart(ctx, {
        type: 'bar',
        data: chartData,
      });

      setShowChart(true);
    }
  };

  const openChartPopup = () => {
    renderChart();
    setShowChart(true);
  };

  const closeChartPopup = () => {
    setShowChart(false);
  };

  return (
    <div>
      <h1>Low Quantity Products</h1>
      <div className="box">
        <table className="table">
          <thead>
            <tr>
              <th>Name</th>
              <th>Type</th>
              <th>Category</th>
              <th>Date</th>
              <th>Remaining Quantity</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {filteredProducts.map((product, index) => (
              <tr key={index}>
                <td>{product.name}</td>
                <td>{product.type}</td>
                <td>{product.category}</td>
                <td>{product.date}</td>
                <td>{product.rquantity}</td>
                <td>
                  <button onClick={openChartPopup}>View Chart</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {showChart && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={closeChartPopup}>&times;</span>
            <canvas ref={chartRef} width="400" height="200" />
          </div>
        </div>
      )}
    </div>
  );
}

export default FilteredProducts;
