import {Form} from 'react-bootstrap';
import { useState } from 'react';
import { useNavigate} from 'react-router-dom';
import axios from 'axios';

function AddTransaction() {

    const navigate = useNavigate();
    const [post, setPost] = useState({
        transaction:"",
        type:"",
        amount:"",
        date:"",
        reference:"",
    });

    const [date, setDate] = useState("");

    const [transactionError, setTransactionError] = useState("");
    const [typeError, setTypeError] = useState("");
    const [amountError, setAmountError] = useState("");
    const [dateError, setDateError] = useState("");
    const [referenceError, setReferenceError] = useState("");

    const validateForm = () => {
        let valid = true;

        const transaction = document.getElementById('transaction');
        const type = document.getElementById('type');
        const amount = document.getElementById('amount');
        const date = document.getElementById('date');
        const reference = document.getElementById('reference');

        if(post.transaction === '') {
            setTransactionError("Please enter a transaction");
            valid = false;
        } else {
            setTransactionError("");
        }

        if(post.type === '') {
            setTypeError("Please select a type");
            valid = false;
        } else {
            setTypeError("");
        }

        if(post.amount === '') {
            setAmountError("Please enter an amount");
            valid = false;
        }else if(isNaN(post.amount)){
            setAmountError("Please enter a valid amount");
            valid = false;
        } else {
            setAmountError("");
        }

        if(post.date === '') {
            setDateError("Please select a date");
            valid = false;
        } else {
            setDateError("");
        }

        if(post.reference === '') {
            setReferenceError("Please enter a reference");
            valid = false;
        } else {
            setReferenceError("");
        }
        
    }

    const handleChange = (event) => {
        const { name, value } = event.target;

        setPost((prev) => {
            return {
                ...prev,
                [name]: value,
            };
        });
    };

    const handleClick = (event) => {
        if (validateForm()) {
            axios.post("/api/Fin/add", post)
            .then((res) => console.log(res))
            .catch((err) => console.log(err))
        }
        navigate("transactions");
    }

    const getDate = () => {
        const date = new Date();
        let currentDay = String(date.getDate()).padStart(2, '0');
        let currentMonth = String(date.getMonth()+1).padStart(2, "0");
        let currentYear = date.getFullYear();
        let currentDate = `${currentYear}-${currentMonth}-${currentDay}`;
        setDate(currentDate);
        setPost({
            transaction:"",
            type:"",
            amount:"",
            date:currentDate,
            reference:"",
        })
    }

    return(
        <div>
            <div className="addTransaction">
                <h1 className="title">Add New Transaction</h1>
                <Form className='form'>
                    <Form.Group>
                        <Form.Control className='form-control'
                            id='transaction'
                            name='transaction'
                            value={post.transaction}
                            placeholder='Enter the Transaction'
                            onChange={handleChange}
                        />

                        <Form.Select className='form-control'
                            id='type'
                            name='type'
                            value={post.type}
                            placeholder='Select the transaction type'
                            onChange={handleChange} >
                                <option selected>Select Type</option>
                                <option>Income</option>
                                <option>Expense</option>
                        </Form.Select>

                        <Form.Control 
                            id='amount'
                            name='amount'
                            value={post.amount}
                            placeholder='Enter the Amount'
                            onChange={handleChange}
                        />

                        <Form.Control 
                            id='date'
                            name='date'
                            value={post.date}
                            placeholder='Enter the Date'
                            onChange={handleChange}
                        />

                        <Form.Control 
                            id='reference'
                            name='reference'
                            value={post.reference}
                            placeholder='Enter the Reference'
                            onChange={handleChange}
                        />
                    </Form.Group>
                    <button onClick={handleClick}>Add Transaction</button>
                </Form>
            </div>
        </div>
    )
}

export default AddTransaction;
