import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Chart from 'chart.js/auto';

function FilteredProducts() {
  const [filteredProducts, setFilteredProducts] = useState([]);
  const navigate = useNavigate();

  // Create a ref to the canvas element
  const chartRef = React.createRef();
  const chartInstance = React.createRef();

  useEffect(() => {
    // Fetch products from your API
    axios.get('/api/Product/products')
      .then((res) => {
        const lowQuantityProducts = res.data.filter(product => product.rquantity < 5);
        setFilteredProducts(lowQuantityProducts);

        // Destroy the previous chart if it exists
        if (chartInstance.current) {
          chartInstance.current.destroy();
        }

        // Create a new chart
        const ctx = chartRef.current.getContext('2d');
        // Extract data for the chart
        const productNames = lowQuantityProducts.map(product => product.name);
        const remainingQuantities = lowQuantityProducts.map(product => product.rquantity);

        // Create the chart
        chartInstance.current = new Chart(ctx, {
          type: 'bar',
          data: {
            labels: productNames,
            datasets: [
              {
                label: 'Remaining Quantity',
                data: remainingQuantities,
                backgroundColor: 'rgba(75, 192, 192, 0.2)',
                borderColor: 'rgba(75, 192, 192, 1)',
                borderWidth: 1,
              },
            ],
          },
          options: {
            scales: {
              y: {
                beginAtZero: true,
              },
            },
          },
        });
      })
      .catch((err) => console.log(err));
  }, []);

  return (
    <div>
      <h1>Low Quantity Products</h1>
      <div className="box">
        <table className="table">
          {/* ... Your table code */}
        </table>
        <div className="chart">
          <canvas ref={chartRef} width="400" height="200"></canvas>
        </div>
      </div>
    </div>
  );
}

export default FilteredProducts;
