import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { useNavigate } from "react-router-dom";
import Chart from 'chart.js/auto';

function FilteredProducts() {
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [showChartModal, setShowChartModal] = useState(false);
  const navigate = useNavigate();
  const chartRef = useRef(null);

  useEffect(() => {
    // Fetch products from your API
    axios.get('/api/Product/products')
      .then((res) => {
        const lowQuantityProducts = res.data.filter(product => product.rquantity < 5);
        setFilteredProducts(lowQuantityProducts);

        // Create data arrays for the chart
        const productNames = lowQuantityProducts.map(product => product.name);
        const productQuantities = lowQuantityProducts.map(product => product.rquantity);

        // Create the chart data
        const chartData = {
          labels: productNames,
          datasets: [
            {
              label: 'Remaining Quantity',
              data: productQuantities,
              backgroundColor: 'rgba(75, 192, 192, 0.2)', // Bar color
              borderColor: 'rgba(75, 192, 192, 1)', // Border color
              borderWidth: 1,
            },
          ],
        };

        // Create the chart after ensuring the chartRef is available
        if (chartRef.current) {
          const ctx = chartRef.current.getContext('2d');
          new Chart(ctx, {
            type: 'bar',
            data: chartData,
          });
        }
      })
      .catch((err) => console.log(err));
  }, []);

  const openChartModal = () => {
    setShowChartModal(true);
  };

  const closeChartModal = () => {
    setShowChartModal(false);
  };

  return (
    <div>
      <h1>Low Quantity Products</h1>
      <div className="box">
        <table className="table">
          <thead>
            <tr>
              <th>Name</th>
              <th>Type</th>
              <th>Category</th>
              <th>Date</th>
              <th>Remaining Quantity</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {filteredProducts.map((product, index) => (
              <tr key={index}>
                <td>{product.name}</td>
                <td>{product.type}</td>
                <td>{product.category}</td>
                <td>{product.date}</td>
                <td>{product.rquantity}</td>
                <td>
                  <button onClick={() => navigate("/sups/SupplierOrd")}>View Suppliers</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <button onClick={openChartModal}>Open Chart</button>

      {/* Chart modal (popup) */}
      {showChartModal && (
        <div className="chart-modal">
          <div className="modal-content">
            <span className="close" onClick={closeChartModal}>&times;</span>
            <canvas ref={chartRef} width="400" height="200" />
          </div>
        </div>
      )}
    </div>
  );
}

export default FilteredProducts;
