import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { useNavigate } from "react-router-dom";
import { Chart } from 'chart.js'; // Import Chart from 'chart.js'

function FilteredProducts() {
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [showChart, setShowChart] = useState(false);
  const navigate = useNavigate();
  const chartRef = useRef(null);

  useEffect(() => {
    // Fetch products from your API
    axios.get('/api/Product/products')
      .then((res) => {
        const lowQuantityProducts = res.data.filter(product => product.rquantity < 5);
        setFilteredProducts(lowQuantityProducts);
      })
      .catch((err) => console.log(err));
  }, []);

  const showChartOnClick = () => {
    setShowChart(true);

    if (chartRef.current) {
      // Create data arrays for the chart
      const productNames = filteredProducts.map(product => product.name);
      const productQuantities = filteredProducts.map(product => product.rquantity);

      // Create the chart data
      const chartData = {
        labels: productNames,
        datasets: [
          {
            label: 'Remaining Quantity',
            data: productQuantities,
            backgroundColor: 'rgba(75, 192, 192, 0.2)', // Bar color
            borderColor: 'rgba(75, 192, 192, 1)', // Border color
            borderWidth: 1,
          },
        ],
      };

      // Create the chart
      const ctx = chartRef.current.getContext('2d');
      new Chart(ctx, {
        type: 'bar',
        data: chartData,
        options: {
          scales: {
            x: {
              title: {
                display: true,
                text: 'Products'
              }
            },
            y: {
              title: {
                display: true,
                text: 'Quantity'
              }
            }
          }
        }
      });
    }
  };

  return (
    <div>
      <h1>Low Quantity Products</h1>
      <div className="box">
        <table className="table">
          <thead>
            <tr>
              <th>Name</th>
              <th>Type</th>
              <th>Category</th>
              <th>Date</th>
              <th>Remaining Quantity</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {filteredProducts.map((product, index) => (
              <tr key={index}>
                <td>{product.name}</td>
                <td>{product.type}</td>
                <td>{product.category}</td>
                <td>{product.date}</td>
                <td>{product.rquantity}</td>
                <td>
                  <button onClick={() => navigate("/sups/SupplierOrd")}>View Suppliers</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Button to show chart */}
      <div>
        <button onClick={showChartOnClick}>View Chart</button>
      </div>

      {/* Conditionally render the chart */}
      {showChart && 
        <div className="chart-container" style={{ width: '80%', margin: '0 auto', border: '1px solid black' }}>
          <canvas ref={chartRef} />
        </div>
      }
    </div>
  );
}


export default FilteredProducts;
