import { Form } from "react-bootstrap";
import {useNavigate} from 'react-router-dom';
import { useEffect,useState } from "react";
import axios from "axios";
import './transactions.css';

function CreatePost () {

    useEffect(() => {
        getDate();
    }, []);

    const navigate = useNavigate();
    const [data, setData] = useState ({
        transaction:"",
        type:"",
        amount:"",
        date:"",
        reference:"",
    });
    const [date, setDate] = useState("");

    // handleChange function to track the value
    const handleChange = (event) => {
        const { name, value } = event.target;

        setData((prev) => {
            return {
                ...prev,
                [name]: value,
            };
        });
    };

    // validation function
    const [transactionError, setTransactionError] = useState("");
    const [typeError, setTypeError] = useState("");
    const [amountError, setAmountError] = useState("");
    const [referenceError, setReferenceError] = useState("");

    const validateForm = () => {
        let valid = true;

        const Transaction = document.getElementById('transaction');
        if(data.transaction === '') {
            Transaction.setCustomValidity('Please enter the Transaction'); // extend the validation
            setTransactionError("Please enter the Transaction");
            valid = false;
        } else {
            Transaction.setCustomValidity('');
            setTransactionError("");
        }

        const Type = document.getElementById('type');
        if (data.type === '') {
            Type.setCustomValidity('Please select a transaction type');
            setTypeError("Please select a transaction type.");
            valid = false;
        } else {
            Type.setCustomValidity('');
            setTypeError("");
        }

        const Reference = document.getElementById('reference');
        if(data.reference === '') {
            Reference.setCustomValidity('Please enter the reference');
            setReferenceError("Please enter the reference");
            valid = false;
        } else {
            Reference.setCustomValidity('');
            setReferenceError("");
        }

        const Amount = document.getElementById('amount');
        if(data.amount === '') {
            Amount.setCustomValidity('Please enter the amount');
            setAmountError("Please enter the amount");
            valid = false;
        }else if (isNaN(data.amount)) {
            Amount.setCustomValidity('Please enter a the amount');
            setAmountError("Please enter a the amount")
            valid = false;
            
        } else {
            Amount.setCustomValidity('');
            setAmountError("");
        }

        return valid;
    }

    // handle click function to post data
    const handleClick = (event) => {
        if (validateForm()) {
            axios.post("/api/Fin/create", data)
            .then((res) => console.log(res))
            .catch((err) => console.log(err));

            console.log(data);

            // navigate("trans");
        }
    };

    // getDate function to display the current date
    const getDate = () => {
        const date = new Date();
        let currentDay= String(date.getDate()).padStart(2, '0');
        let currentMonth = String(date.getMonth()+1).padStart(2,"0");
        let currentYear = date.getFullYear();
        let currentDate = `${currentYear}-${currentMonth}-${currentDay}`;
        setDate(currentDate);
        setData({
            transaction:"",
            type:"",
            amount:"",
            date:currentDate,
            reference:"",
        })
    }

    return (
        <div className="finance-create">
            <div className="Create-post">
                <h3 className="title">Add Transactions</h3><br />
                <Form className="Form">
                    <Form.Group className="Form-Group">
                        <Form.Control className="Form-Control" 
                            id="transaction"
                            name="transaction" 
                            value={data.transaction}
                            placeholder="Enter the Transaction"
                            onChange={handleChange}
                            style={{width:"80%", marginLeft:"10%"}}
                            required />
                        { transactionError && <div className="error" style={{marginLeft:"10%", marginTop:"-3.5%"}}>{transactionError}</div> }
                        
                        <Form.Select className="Form-Control" 
                            id="type"
                            name="type"
                            value={data.type} 
                            placeholder="Select the Transaction Type"
                            onChange={handleChange} 
                            style={{width:"80%", marginLeft:"10%"}}
                            required>
                                <option>Transaction Type</option>
                                <option>Income</option>
                                <option>Expense</option>       
                        </Form.Select>
                        { typeError && <div className="error" style={{marginLeft:"10%", marginTop:"-3.5%"}}>{typeError}</div> }

                        <Form.Control className="Form-Control" 
                            id="amount"
                            name="amount"
                            value={data.amount} 
                            placeholder="Enter the Amount"
                            onChange={handleChange} 
                            style={{width:"80%", marginLeft:"10%"}}
                            required>
                        </Form.Control>
                        { amountError && <div className="error" style={{marginLeft:"10%", marginTop:"-3.5%"}}>{amountError}</div> }

                        <Form.Control className="Form-Control"
                            id="date"
                            name="date" 
                            value={data.date}
                            placeholder="Enter the Date"
                            onChange={handleChange} 
                            style={{width:"80%", marginLeft:"10%"}}
                            readOnly={true}
                            required />

                        <Form.Control className="Form-Control"
                            id="reference"
                            name="reference" 
                            value={data.reference}
                            placeholder="Enter the Reference"
                            onChange={handleChange} 
                            style={{width:"80%", marginLeft:"10%"}}
                            required />
                        { referenceError && <div className="error" style={{marginLeft:"10%", marginTop:"-3.5%"}}>{referenceError}</div> }
                    </Form.Group>
                    <br />
                    < button style={{borderRadius:"5px", background:"#b30059", padding:"1.5%", width:"45%", fontSize:"17px", 
                    paddingLeft:"5px", paddingRight:"5px", border:"#b30059"}} onClick={handleClick}>ADD TRANSACTION</button>
                </Form>
            <br />
        </div>
        </div>
    );
}

export default CreatePost;
