import { useEffect,useState } from "react";
import axios from "axios";
import {Form, InputGroup } from "react-bootstrap";
// import {useNavigate } from "react-router-dom";
import Modal from 'react-bootstrap/Modal';
import { Link } from "react-router-dom";
import './supplier.css';
import emailjs from 'emailjs-com';


function App() {
// const navigate = useNavigate();


// const navigate = useNavigate();
const [posts, setPosts] = useState([]);
const [updatedPost, setUpdatedPost] = useState({})
const [search, setSearch] = useState('');

const [show, setShow] = useState(false);
const handleClose = () => setShow(false);
const handleShow = () => setShow(true);

useEffect(() => {
    axios.get("/api/Sup/sups")
        .then((res) => {
            console.log(res)
            setPosts(res.data);
        })
        .catch((err) => console.log(err));
}, []);

const deletePost = (id) => {
axios
.delete(`/api/Sup/delete/${id}`)
.then((res) => console.log(res))
.catch((err) => console.log(err));

window.location.reload();
};

const updatePost = (post) => {
setUpdatedPost(post);
handleShow();
}

const handleChange = (e) => {
const { name, value} = e.target;

setUpdatedPost((prev) => {
    return {
        ...prev,
        [name]: value,
    };
});
};

const saveUpdatedPost = () => {
axios.put(`/api/Sup/update/${updatedPost._id}`, updatedPost)
.then((res) => console.log(res))
.catch((err) => console.log(err));

handleClose();
window.location.reload();
};

const [date,setdate] = useState();
const [quantity,setquantity] = useState();


// script for mailing
function SendEmail(){
    console.log(quantity);
    console.log(date);
    var templateParams = {
        //  from_name: document.getElementById('name').value,
        to_name:"Dear Supplier",
        // email: document.getElementById("product").value,
        // mobile: document.getElementById("contact").value,
        // subject: document.getElementById("email").value,
        // status: document.getElementById("status").value,
        date :date,
        quantity: quantity,


    };
     
    const sendEmail = (e) => {
        e.preventDefault();
    
        emailjs.sendForm('gmail', 'template_9xjli46', e.target, 'wHDsg4i00KtMChfC5')
          .then((result) => {
              console.log(result.text);
          }, (error) => {
              console.log(error.text);
          }); 

}

//Sorting function
const [order, setOrder] = useState("ASC");
const sorting = (col) =>{
  if(order ==="ASC"){
    const sorted = [...posts].sort((a,b) =>
        a[col].toLowerCase() < b[col].toLowerCase() ? 1 : -1);

        setPosts(sorted);
        setOrder("DESC");
  }
  if(order ==="DESC"){
    const sorted = [...posts].sort((a,b) =>
        a[col].toLowerCase() > b[col].toLowerCase() ? 1 : -1);

        setPosts(sorted);
        setOrder("ASC");
    }
  };



  return (
    <div className="product">
    <div>
        {/* style={{width:"90%", margin: "auto auto", textAlign: "center"}} */}
        
      <br /><br />
        {/* <button onClick={() => navigate(-1)}>BACK</button> */}
        <Modal show={show} onHide={handleClose} >
            <Modal.Header closeButton>
                <Modal.Title style={{color:"#373B61"}}>Make Order Request</Modal.Title>
            </Modal.Header>
            <Modal.Body style={{width:"100%", height:"200%"}}>
                <Form>
                    <Form.Group>
                        <Form.Control 
                            style={{width: "80%",
                                    padding: "6px 10px",
                                    margin: "10px 0",
                                    border: "1px solid #373B61",
                                    borderRadius: "5px",
                                    boxSizing: "border-box",
                                    display: "block",
                                    id:"name",
                                    marginLeft: "10%"}}
                            placeholder="name"
                            name="name"
                            value={updatedPost.name ? updatedPost.name : ""}
                            onChange={handleChange}
                            readOnly={true}
                            />
                        <Form.Control 
                            style={{width: "80%",
                            padding: "6px 10px",
                            margin: "10px 0",
                            border: "1px solid #373B61",
                            borderRadius: "5px",
                            boxSizing: "border-box",
                            display: "block",
                            id:"product",
                            marginLeft: "10%"}}
                            placeholder="Product"
                            name="product"
                            value={updatedPost.product ? updatedPost.product : ""}
                            onChange={handleChange}
                            readOnly={true}
                            />
                        <Form.Control 
                            style={{width: "80%",
                            padding: "6px 10px",
                            margin: "10px 0",
                            border: "1px solid #373B61",
                            borderRadius: "5px",
                            boxSizing: "border-box",
                            display: "block",
                            id:"contact",
                            marginLeft: "10%"}}
                            placeholder="Contact"
                            name="contact"
                            value={updatedPost.contact ? updatedPost.contact : ""}
                            onChange={handleChange}
                            readOnly={true}
                            />
                        <Form.Control 
                            style={{width: "80%",
                            padding: "6px 10px",
                            margin: "10px 0",
                            border: "1px solid #373B61",
                            borderRadius: "5px",
                            boxSizing: "border-box",
                            display: "block",
                            id:"email",
                            marginLeft: "10%"}}
                            placeholder="email"
                            name="email"
                            value={updatedPost.email ? updatedPost.email : ""}
                            onChange={handleChange}
                            readOnly={true}
                            />
                        <Form.Control 
                            style={{width: "80%",
                            padding: "6px 10px",
                            margin: "10px 0",
                            border: "1px solid #373B61",
                            borderRadius: "5px",
                            boxSizing: "border-box",
                            display: "block",
                            id:"status",
                            marginLeft: "10%"}}
                            placeholder="status"
                            name="status"
                            value={updatedPost.status ? updatedPost.status : ""}
                            onChange={handleChange}
                            readOnly={true}
                            />
                        <Form.Control 
                            style={{width: "80%",
                            padding: "6px 10px",
                            margin: "10px 0",
                            border: "1px solid #373B61",
                            borderRadius: "5px",
                            boxSizing: "border-box",
                            display: "block",
                            id:"date",
                            marginLeft: "10%"}}
                            placeholder="Date"
                            name="date"
                            type="date"
                            //value={updatedPost.date ? updatedPost.date : ""}
                            onChange={(e) => setdate(e.target.value)}/>
                        <Form.Control 
                            style={{width: "80%",
                            padding: "6px 10px",
                            margin: "10px 0",
                            border: "1px solid #373B61",
                            borderRadius: "5px",
                            boxSizing: "border-box",
                            display: "block",
                            id:"quantity",
                            marginLeft: "10%"}}
                            placeholder="Quantity"
                            name="quantity"
                            //value={updatedPost.quantity ? updatedPost.quantity : ""}
                            onChange={(e) => setquantity(e.target.value)}/>
                    </Form.Group>
                </Form>
            </Modal.Body>
            <Modal.Footer>
                <button style={{borderRadius:"5px", background:"#373B61",color:"#EDEFFE", padding:"1.5%", width:"200px", fontSize:"17px", 
                border:"#373B61", marginRight:"25%"}} onClick={handleClose}>
                    Close
                </button>
                <br />
                <button style={{borderRadius:"5px", background:"#373B61", padding:"1.5%",color:"#EDEFFE", width:"200px", fontSize:"17px", 
                border:"#373B61", marginRight:"25%"}} onClick={SendEmail}>
                    Place Order
                </button>
            </Modal.Footer>
        </Modal>

        {posts ? (
            
            <div>
            
            <Form>
                <InputGroup className="my-1" style={{width:"20%", marginLeft:"75%"}}>
                    <Form.Control 
                    onChange={(e) => setSearch(e.target.value)}
                    placeholder="Search here"/>
                </InputGroup>
            </Form>
            <br />
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                {/* <button style={{borderRadius:"5px", background:"#373B61", padding:"0.5%"}}><Link to="/sup/add" style={{color:"white", textDecoration:"none"}}>Add New Supplier</Link></button>&nbsp;&nbsp;&nbsp;&nbsp;
                <button style={{borderRadius:"5px", background:"#373B61", padding:"0.5%"}}><Link to="/sup/report" style={{color:"white", textDecoration:"none"}}>Download Supplier List</Link></button>&nbsp;&nbsp;&nbsp;&nbsp; */}
                                
                <br /><br />
                <center>
                    <h1 style={{color:"#373B61", fontWeight:"bolder", fontSize:"50px"}}>Make order</h1>
                </center>

                <div className="container">
                {/* <button onClick={() => sorting("product")}>Sort by Product</button>&nbsp;
                <button onClick={() => sorting("status")}>Sort by Status</button> */}
                </div>
                <br />

                <div className="container">
                    
                <table class="table">
                <thead>
                  <tr>
                  <th scope="col">ID</th>
                  <th scope="col">Name</th>
                  <th scope="col">Product</th>
                  <th scope="col">Contact</th>
                  <th scope="col">Email</th>
                  <th scope="col">Status</th>
                  <th scope="col">Date</th>
                  <th scope="col">Quantity</th>
                  <th scope="col">Price</th>
                  <th></th>
                  <th scope="col">Action</th>
                  </tr>
                </thead>

                    
                        {posts.filter((post) => {
                            return search.toLowerCase() === ''
                                ? post
                                : post.name.toLowerCase().includes(search) ||
                                  post.type.toLowerCase().includes(search) ||
                                  post.category.toLowerCase().includes(search) 
                                  //post.date.toLowerCase().includes(search) ||
                                  //post.quantity.toLowerCase().includes(search) ||
                                 // post.totalPrice.toLowerCase().includes(search)
                        })
                        .map((post, index) => {
                    return (

                        <tbody>
                        <tr>
                        <td>{index+1}</td>
                        <td>{post.name}</td>
                        <td>{post.product}</td>
                        <td>{post.contact}</td>
                        <td>{post.email}</td>
                        <td>{post.status}</td>
                        <td>{post.date}</td>
                        <td>{post.quantity}</td>
                        <td>{post.price}</td>
                        <td >
                        
                        <button  style={{width: "70%",
                                    marginLeft:'10px'                   
                        }} onClick={() => updatePost(post)}>Order</button>   </td>

                        {/* <td>
                        <button style={{width: "80%", marginLeft:'-20%', marginTop:""}} onClick={() => deletePost(post._id)}>DELETE</button>
                        </td> */}
                        
                        
                        </tr>
                        </tbody>
                    );
                })}
             </table>
                </div>

                
            </div>
        ) : (
          ""
        )}
    </div>
    </div>
);
}
}


export default App;
