import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from "react-router-dom";
import { Pie } from 'react-chartjs-2';

function FilteredProducts() {
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [chartData, setChartData] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch products from your API
    axios.get('/api/Product/products')
      .then((res) => {
        const lowQuantityProducts = res.data.filter(product => product.rquantity < 5);
        setFilteredProducts(lowQuantityProducts);

        // Prepare data for the pie chart
        const chartData = {
          labels: lowQuantityProducts.map(product => product.name),
          datasets: [{
            data: lowQuantityProducts.map(product => product.rquantity),
            backgroundColor: [
              'red', 'blue', 'green', 'orange', 'purple', // Add more colors if needed
            ],
          }],
        };
        setChartData(chartData);
      })
      .catch((err) => console.log(err));
  }, []);

  return (
    <div>
      <h1>Low Quantity Products</h1>
      <div className="box">
        <table className="table">
          <thead>
            <tr>
              <th>Name</th>
              <th>Type</th>
              <th>Category</th>
              <th>Date</th>
              <th>Remaining Quantity</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {filteredProducts.map((product, index) => (
              <tr key={index}>
                <td>{product.name}</td>
                <td>{product.type}</td>
                <td>{product.category}</td>
                <td>{product.date}</td>
                <td>{product.rquantity}</td>
                <td>
                  <button onClick={() => navigate("/sups/SupplierOrd")}>View Suppliers</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      
      {Object.keys(chartData).length > 0 && (
        <div className="chart">
          <Pie data={chartData} />
        </div>
      )}
    </div>
  );
}

export default FilteredProducts;
